mapper.update:
UPDATE TBL_BOARD
SET BRDTITLE = #{brdtitle}, BRDMEMO = #{brdmemo}, BRDWRITER = #{brdwriter}
WHERE BRDNO = #{brdno} AND BRDDELETEFLAG = 'N'

mapper.delete:
UPDATE TBL_BOARD
SET BRDDELETEFLAG='Y'
WHERE BRDNO = #{brdno}

mapper.update:
UPDATE TBL_BOARD
SET BRDHIT = BRDHIT + 1
WHERE BRDNO = #{brdno}
