mapper.update:
UPDATE TBL_BOARD
SET BRDTITLE = #{brdtitle}, BRDMEMO = #{brdmemo}, BRDWRITER = #{brdwriter}
WHERE BRDNO = #{brdno} AND BRDDELETEFLAG = 'N'

mapper.delete:
UPDATE TBL_BOARD
SET BRDDELETEFLAG='Y'
WHERE BRDNO = #{brdno}

mapper.update:
UPDATE TBL_BOARD
SET BRDHIT = BRDHIT + 1
WHERE BRDNO = #{brdno}

mapper.delete:
DELETE
FROM TBL_BOARDFILE
WHERE FILENO IN (

    ${item}

)

mapper.update:
UPDATE TBL_BOARDREPLY
SET REDELETEFLAG='Y'
WHERE RENO=#{reno}

UPDATE TBL_BOARDREPLY
SET REMEMO=#{rememo}
WHERE RENO=#{reno}
